import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        TriangleEdgesCheck check = new TriangleEdgesCheck();        try {            System.out.print("Nhập cạnh thứ 1: ");            int a = sc.nextInt();            System.out.print("Nhập cạnh thứ 2: ");            int b = sc.nextInt();            System.out.print("Nhập cạnh thứ 3: ");            int c = sc.nextInt();            try {                check.checkTriangle(a, b, c);            } catch (IllegalTriangleException e) {                System.out.println(e.getMessage());            }        } catch (Exception e) {            System.out.println("Sai, không đúng định dạng!");        }    }}